/* eslint-disable jsx-a11y/accessible-emoji */
import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,Link
} from "react-router-dom";

import "./Header.css";
import { CSSTransition } from "react-transition-group";



var textWrapper = document.querySelector('.ml3');
textWrapper.innerHTML = textWrapper.textContent.replace(/\S/g, "<span class='letter'>$&</span>");

anime.timeline({loop: true})
  .add({
    targets: '.ml3 .letter',
    opacity: [0,1],
    easing: "easeInOutQuad",
    duration: 2250,
    delay: (el, i) => 150 * (i+1)
  }).add({
    targets: '.ml3',
    opacity: 0,
    duration: 1000,
    easing: "easeOutExpo",
    delay: 1000
  });
export default function Header() {
  const [isNavVisible, setNavVisibility] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(false);
  const [pubKey, setPubKey] = useState('');

  useEffect(() => {
    const mediaQuery = window.matchMedia("(max-width: 700px)");
    mediaQuery.addListener(handleMediaQueryChange);
    handleMediaQueryChange(mediaQuery);

    return () => {
      mediaQuery.removeListener(handleMediaQueryChange);
    };
  }, []);

  const handleMediaQueryChange = mediaQuery => {
    if (mediaQuery.matches) {
      setIsSmallScreen(true);
    } else {
      setIsSmallScreen(false);
    }
  };

  const toggleNav = () => {
    setNavVisibility(!isNavVisible);
  };

  useEffect(()=>{
    console.log(window.ethereum.selectedAddress)
    if(window.ethereum.selectedAddress!==null){
      setPubKey(window.ethereum.selectedAddress)
    }
  })

  const handleConnect = (e) =>{
    e.preventDefault();
    if (typeof window !== "undefined") {
        try {
          window.ethereum.enable().then(async()=> {
            setPubKey(window.ethereum.selectedAddress)
          })
        }
        catch(e){
            alert("please install metamask!");
        }
  }
}

  return (
    <header className="Header">
      <Link to="/"><img src={require("../../assets/logo.png")} className="Logo" alt="logo" /></Link>
      <CSSTransition
        in={!isSmallScreen || isNavVisible}
        timeout={350}
        classNames="NavAnimation"
        unmountOnExit
      >
        <nav className="Nav">
            <div>
                  <Link to="/">BUY</Link>
                  <Link to="/exchange">EXCHANGE</Link>
                  <Link to="/referral" style={{color:"red"}}>Invite Friends</Link>
            </div>
          <button onClick = {handleConnect}>{window.ethereum?window.ethereum.selectedAddress!==null?`${pubKey.slice(0,6)}...`:"CONNECT":"please install!"}</button>
        </nav>
      </CSSTransition>
      <button onClick={toggleNav} className="Burger">
        üçî
      </button>
    </header>
  );
}
