import {useEffect, useState} from 'react';
import {Radio, Grid} from '@material-ui/core';
import { ethers } from 'ethers';
import {
    BrowserRouter as Route, Switch ,Link
  } from "react-router-dom";

import Spot from "./spot";
import './buy.css';
import '../../bootstrap.min.css';


export default function Buy() {


    return (
        <div>
            <div>
            <Link to="?spot/"><button className="button-spot-index">Buy from SPOT</button></Link>
            <Link to="?fiat/"><button className="button-fiat-index">Buy from FIAT</button></Link>
            </div>
            {/* <Spot /> */}
            <Switch className='Content'>
                <Route exact path="?spot/" component={Spot} />
                <Route exact path="?fiat/" component={Fiat} />      
            </Switch>
        </div>
    );
}
