/* eslint-disable jsx-a11y/accessible-emoji */
import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,Link
} from "react-router-dom";

import "./Header.css";
import { CSSTransition } from "react-transition-group";



export default function Header() {
  const [isNavVisible, setNavVisibility] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(false);
  const [pubKey, setPubKey] = useState('');

  useEffect(() => {
    const mediaQuery = window.matchMedia("(max-width: 700px)");
    mediaQuery.addListener(handleMediaQueryChange);
    handleMediaQueryChange(mediaQuery);

    return () => {
      mediaQuery.removeListener(handleMediaQueryChange);
    };
  }, []);

  const handleMediaQueryChange = mediaQuery => {
    if (mediaQuery.matches) {
      setIsSmallScreen(true);
    } else {
      setIsSmallScreen(false);
    }
  };

  const toggleNav = () => {
    setNavVisibility(!isNavVisible);
  };

  useEffect(()=>{
    console.log(window.ethereum.selectedAddress)
    if(window.ethereum.selectedAddress!==null){
      setPubKey(window.ethereum.selectedAddress)
    }
  })

  const handleConnect =async () =>{
    console.log("connect");
    if(typeof window !== "undefined"){
        if (window.ethereum) {
            console.log("good");
            const chainId = await ethereum.request({ method: 'eth_chainId' });
            if(!flag&&chainId==1)
                window.ethereum.enable().then((res)=> {
                    setFlag(true);
                }).catch((err)=> {
                    setFlag(false);
                })
            if(chainId!=1){
                alert("Please change network to ethereum main net")
            }
            }
        else{
            alert("you have to install metamask!")
        }
    }
}
  return (
    <header className="Header">
      <Link to="/"><img src={require("../../assets/logo.png")} className="Logo" alt="logo" /></Link>
      <CSSTransition
        in={!isSmallScreen || isNavVisible}
        timeout={350}
        classNames="NavAnimation"
        unmountOnExit
      >
        <nav className="Nav">
            <div>
                  <Link to="/" className="router-link">BUY</Link>
                  <Link to="/exchange" className="router-link">EXCHANGE</Link>
                  <Link to="/referral" className="test">Invite Friends</Link>
            </div>
          <button onClick = {handleConnect}>{flag?"`${pubKey.slice(0,6)}...`":"connect"}</button>
        </nav>
      </CSSTransition>
      <button onClick={toggleNav} className="Burger">
        üçî
      </button>
    </header>
  );
}
